import { useEffect, useState } from 'react';
import styles from './app.module.css';
import Searchheader from './components/search_header/search_header';
import VideoList from './components/video_list/video_list';

function App() {
  const [videos, setVideos]= useState([]);
  const search = query =>{
    const requestOptions = {
      method: 'GET',
      redirect: 'follow'
    };

    fetch(`https://www.googleapis.com/youtube/v3/search?key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g&part=snippet&maxResults=25&q=${query}&type=video&key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g`, requestOptions)
      .then(response => response.json())
      .then(result => result.items.map(item => ({...item, id :  item.id.videoID})))
      .then(items => setVideos(items))
      .catch(error => console.log('error', error));
  }

  useEffect(() =>{
    const requestOptions = {
      method: 'GET',
      redirect: 'follow'
    };

    fetch("https://www.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=100&key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g"
    , requestOptions)
      .then(response => response.json())
      .then(result => setVideos(result.items))
      .catch(error => console.log('error', error));
  },[])


  return (
    <div className ={styles.app}>
    <Searchheader onSearch={search}/>
    <VideoList videos = {videos} />
    </div>
  );
}

export default App;
