class Youtube{
    constructor(key){
        this.key = key;
        this.getRequestOptions ={
            method: 'GET',
            redirect: 'follow'    
        }
    }
    mostPopular(){
      return fetch(
            "https://www.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=100&key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g"
          , this.getRequestOptions)
            .then(response => response.json())
            .then(result => setVideos(result.items))
    }

    search(query){
      return fetch(`https://www.googleapis.com/youtube/v3/search?key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g&part=snippet&maxResults=25&q=${query}&type=video&key=AIzaSyD_EZJ9_I97yVPGl5NZJU9q9e4dJxUX58g`, requestOptions)
        .then(response => response.json())
        .then(result => result.items.map(item => ({...item, id :  item.id.videoID})))
        .then(items => setVideos(items))
    }
}
export default Youtube;
